public class Qn15b {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		   System.out.println("Enter the number : ");
		   int n = sc.nextInt(); //N x N Square 
		   
		   
		   for(int i =1; i <=n ; i ++)
			   
			   for(int j =1; j <=n ; i ++) {
				   
				   System.out.print("*");
				   
			   }
		   System.out.print("");
	}

}
